import React, { Component } from 'react';
import './Collect.css';
import Table from './Table';
import Form from './Form';
import {withRouter} from 'react-router-dom';
  



class Collect extends Component {
  constructor(){
    console.log("hello constractor");
  }
    state = {
        id: 1,
        username: '',
        phone: '',
        startDate: '',
        endDate: '',
        action: '',
        items: [],
        peaple:[]
      }

  handleFormSubmit = (e) => {
    e.preventDefault();


    let items = [...this.state.items];

    items.push({
      id: this.state.id,
      username: this.state.username,
      phone: this.state.phone,
      startDate: this.state.startDate,
      endDate: this.state.endDate,
      action: this.state.action,




    });

    this.setState({
      items,
      id: this.state.id + 1,
      username: '',
      phone: '',
      startDate: '',
      endDate: '',
      action: '',


    });
  };

  handleInputChange = (e) => {
    let input = e.target;
    let name = e.target.name;
    let value = input.value;

    this.setState({
      [name]: value
    })

  };
  handelDelte = (e) => {
    var newItems = this.state.items.filter((_item) => {
      return _item != e
    });

    this.setState({
      items: newItems,

    });
  }
  disableButton = (test) => {
    if (test.target.value.length > 0) {
      this.setState({ disable: false });
    }
    else {
      this.setState({ disable: true });
    }
    console.log(test.length)

  }

 componentDidMount(){
   /*
   const url = ("http://localhost:5000/select")
   const response = await fetch(url);
   const data = await response.json();
   this.setState({peaple: data.results});*/

   console.log("inside handleGetJson"); 
   //fetch("http://localhost:5000/select", { headers : { 'Content-Type': 'application/json', 'Accept': 'application/json' } }) .then((response) => response.json()) .then((messages) => {console.log("messages");}); 
   
  }


  render() {
    return (
      <div className="Collect">
        <div>
     
      {/*<div>
            {this.state.peaple.map(person =>(
              <div>
              <div>{person.name.title}</div>
              <div>{person.name.frist}</div>
              <div>{person.name.last}</div>
              <img src={person.picture.large}/>
              </div>
            ))}
          </div> */}
          <div>
            
          </div>

          

     
        
        
          <Form
            handleFormSubmit={this.handleFormSubmit}
            handleInputChange={this.handleInputChange}
            newPhone={this.state.phone}
            newStartDate={this.state.startDate}
            newEndDate={this.state.endDate}
          />

          <Table
            items={this.state.items}
            handelDelete={this.handelDelte}/>
        </div>
      </div>
    );
  }
}

export default withRouter(Collect) ;
