import React, { Component } from 'react';
import './App.css';
import Table from './Table';
import Form from './Form';


class App extends Component {
  constructor() {
    super();

    this.state = {
      id: '',
      username: '',
      phone: '',
      startDate: '',
      endDate: '',
      action: '',
      items: [],
      disable:''
    }
  };

  handleFormSubmit = (e) => {
    e.preventDefault();

    let items = [...this.state.items];

    items.push({
      password: this.state.id,
      username: this.state.username,
      phone: this.state.phone,
      startDate: this.state.startDate,
      endDate: this.state.endDate,
      action: this.state.action,
      disable:true
    });

    this.setState({
      items,
      id: '',
      username: '',
      phone: '',
      startDate: '',
      endDate: '',
      action: ''
    });
  };

  handleInputChange = (e) => {
    let input = e.target;
    let name = e.target.name;
    let value = input.value;

    this.setState({
      [name]: value
    })
    if(e.target.value.length > 0){
      this.setState({disable:false});
    }
    else{
      this.setState({disable:true});
    }
  };
  handelDelte = (itemToBeDeleted) => {
    var newItems =this.state.items.filter((_item)=>{
      return _item != itemToBeDeleted
    });
    this.setState({items:newItems});
  }


  render() {
    return (
      <div className="App">
        <div className='logo'>
        <strong className='text'>Active GYM</strong>
        </div>
        <button className='button button1'>Notification</button>
        <Form handleFormSubmit={this.handleFormSubmit}
          handleInputChange={this.handleInputChange}
          newUsername={this.state.username}
          newPhone={this.state.phone}
          newStartDate={this.state.startDate}
          newEndDate={this.state.endDate}
          newAction={this.state.action}
          disable={this.state.disable} />


        <Table items={this.state.items} 
        handelDelete = {this.handelDelte.bind(this)}
        />

      </div>
    );
  }
}

export default App;
