import React, { Component } from 'react';
import './App.css';
import Table from './Table';
import Form from './Form';




class App extends Component {

  state = {
    id: 1 ,
    username: '',
    phone: '',
    startDate: '',
    endDate: '',
    action: '',
    items: [],
  }

  handleFormSubmit = (e) => {
    e.preventDefault();


    let items = [...this.state.items];

    items.push({
      id:this.state.id,
      username: this.state.username,
      phone: this.state.phone,
      startDate: this.state.startDate,
      endDate: this.state.endDate,
      action: this.state.action,




    });

    this.setState({
      items,
      id: this.state.id + 1 ,
      username: '',
      phone: '',
      startDate: '',
      endDate: '',
      action: '',

    });
  };

  handleInputChange = (e) => {
    let input = e.target;
    let name = e.target.name;
    let value = input.value;

    this.setState({
      [name]: value
    })

  };
  handelDelte = (e) => {
    var newItems = this.state.items.filter((_item) => {
      return _item != e
    });

    this.setState({ items: newItems ,

    });
  }
  disableButton = (test) => {
    if (test.target.value.length > 0) {
      this.setState({ disable: false });
    }
    else {
      this.setState({ disable: true });
    }
    console.log(test.length)

  }



  render() {
    return (
      <div className="App">
        <div className='logo'>
          <strong className='text'> <h1>Active GYM</h1></strong>
        </div>



        <Form handleFormSubmit={this.handleFormSubmit}
          handleInputChange={this.handleInputChange}
          newUsername={this.state.username}
          newPhone={this.state.phone}
          newStartDate={this.state.startDate}
          newEndDate={this.state.endDate}
          newAction={this.state.action}
          incrementid = {this.state.id}

        />


        <Table items={this.state.items}
          handelDelete={this.handelDelte.bind(this)}
        />

      </div>
    );
  }
}

export default App;
