        phone: '',
        startDate: '',
        endDate: '',
        action: '',
        items: [],
        peaple:[]
      }

  handleFormSubmit = (e) => {
    e.preventDefault();
    

    let items = [...this.state.items];
    

    items.push({
      id: this.state.id,
      username: this.state.username,
      phone: this.state.phone,
      startDate: this.state.startDate,
      endDate: this.state.endDate,
      action: this.state.action,




    });
    

    this.setState({
      items,
      id: this.state.id + 1,
      username: '',
      phone: '',
      startDate: '',
      endDate: '',
      action: '',


    });
    
  
 fetch('http://localhost:5000/addmember1', {
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    item: this.state.items,
   
  })
})
.then(res => res.json())
.then(data => console.log(data))
//.catch(err => console.log(err);
};

  handleInputChange = (e) => {
    let input = e.target;
    let name = e.target.name;
    let value = input.value;

    this.setState({
      [name]: value
    })

  };
  handelDelte = (e) => {
    var newItems = this.state.items.filter((_item) => {
      return _item != e
    });

    this.setState({
      items: newItems,

    });
  }
  disableButton = (test) => {
    if (test.target.value.length > 0) {
      this.setState({ disable: false });
    }
    else {
      this.setState({ disable: true });
    }
    console.log(test.length)

  }
  componentWillMount() {
    console.log("Hello");
    }

  componentDidMount(){
