class NewPage extends React.Component {
    render() {
      return (
        <div onClick={this.handleClick}>
          <input ref={input => this.inputElement = input} />
        </div>
      );
    }
  
    handleClick = (e) => {
      this.inputElement.click();
    }
  }
  export default NewPage;
