import React, { Component } from 'react';
import './Collect.css';
import Table from './Table';
import Form from './Form';
import { withRouter } from 'react-router-dom';
import axios from 'axios';



class Collect extends Component {

  state = {
    id: 1,
    username: '',
    phone: '',
    startDate: '',
    endDate: '',
    action: '',
    items: [],
    peaple: []
  }

  handleFormSubmit = (e) => {
    e.preventDefault();
    console.log(this.state.username);

    let items = [...this.state.items];

    axios.post(`http://localhost:5000/addmember2`, {
      username: this.state.username, phone: this.state.phone, startDate: this.state.startDate, endDate: this.state.endDate,
    })
      .then(res => {
        this.api();
      })

  };

  handleInputChange = (e) => {
    let input = e.target;
    let name = e.target.name;
    let value = input.value;

    this.setState({
      [name]: value
    })

  };
  handelDelte = (e) => {

    // const arrayCopy = this.state.items.filter((row) => row.id !== e);
    // this.setState({items: arrayCopy});
    console.log(e.id);
    axios.delete('http://localhost:5000/delete/' + e.id);

    // console.log(e);
    // var newItems = this.state.items.filter((_item) => {
    //   return _item != e
    // });


    this.api();
  }
  disableButton = (test) => {
    if (test.target.value.length > 0) {
      this.setState({ disable: false });
    }
    else {
      this.setState({ disable: true });
    }
    console.log(test.length)

  }
  componentWillMount() {
    console.log("Hello");
  }

  api() {
    fetch(`http://localhost:5000/select`,
      { headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' } })
      .then((response) => response.json())
      .then((messages) => {
        console.log(this.setState({ peaple: messages["res"] })
        );
      });
  }



  componentDidMount() {
    /*
    const url = ("http://localhost:5000/select")
    const response = await fetch(url);
    const data = await response.json();
    this.setState({peaple: data.results});
    console.log(data.results);
    */
    console.log("inside handleGetJson");
    this.api();
    //   fetch(`http://localhost:5000/select`, 
    //   { headers : { 'Content-Type': 'application/json', 'Accept': 'application/json' } }) 
    //   .then((response) => response.json())
    //    .then((messages) => {console.log( this.setState({peaple:messages["res"] })
    //  );});


  }
  a = function createBlogPost(data) {
    return fetch('http://api.symfony-3.dev/app_dev.php/posts', {
      method: 'POST',
      mode: 'CORS',
      body: JSON.stringify(data),
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => {
      return res;
    }).catch(err => err);
  }


  render() {

    return (
      <div className="Collect">
        <div>



          {/*<div>
            {this.state.peaple.map(person =>(
              <div>
              <div>{person.name.title}</div>
              <div>{person.name.frist}</div>
              <div>{person.name.last}</div>
              <img src={person.picture.large}/>
              </div>
            ))}
          </div> */}
          <div>

          </div>






          <Form
            handleFormSubmit={this.handleFormSubmit}
            handleInputChange={this.handleInputChange}
            newPhone={this.state.phone}
            newStartDate={this.state.startDate}
            newEndDate={this.state.endDate}
          />

          <Table


            items={this.state.peaple}
            handelDelete={this.handelDelte} />
        </div>
      </div>
    );
  }
}

export default withRouter(Collect);
